package com.ifba.exemplos;

public class ExNomeCemVezes {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		for(int i = 1;i <= 100; i++){
			System.out.println("Jonathan ["+i+"]");
		}
		
		int a = 1;
		while(a < 100){
			System.out.println("Jonathan [WHILE]["+a+"]");
			a++;
		}

	}

}
